import React, { useState, useRef } from 'react';
import TrackCard from './TrackCard';
import '../styles/Playlist.css';

function Playlist({ tracks }) {
  const [activeTrackIndex, setActiveTrackIndex] = useState(null);

  const handlePlay = (index) => {
    setActiveTrackIndex(index);
  };

  const handleStop = () => {
    setActiveTrackIndex(null);
  };

  const handleNext = () => {
    if (activeTrackIndex !== null && activeTrackIndex < tracks.length - 1) {
      setActiveTrackIndex(activeTrackIndex + 1);
    } else {
      setActiveTrackIndex(0);
    }
  };

  const handlePrevious = () => {
    if (activeTrackIndex !== null && activeTrackIndex > 0) {
      setActiveTrackIndex(activeTrackIndex - 1);
    } else {
      setActiveTrackIndex(tracks.length - 1);  // Loop to the last track if at the beginning
    }
  };

  return (
    <div className="playlist">
      <h2>Generated Playlist</h2>
      <div className="track-list">
        {tracks.map((track, index) => (
          <TrackCard 
            key={track.id} 
            track={track} 
            isActive={index === activeTrackIndex}
            onPlay={() => handlePlay(index)}
            onStop={handleStop}
            onNext={handleNext}
            onPrevious={handlePrevious}
          />
        ))}
      </div>
      <div className="playlist-controls">
        <button onClick={handlePrevious}>⏮</button>
        <button onClick={handleNext}>⏭</button>
      </div>
    </div>
  );
        }
export default Playlist;
