import React, { useState, useRef } from 'react';
import TrackCard from './TrackCard';
import '../styles/Playlist.css';

function Playlist({ tracks }) {
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTrackIndex, setCurrentTrackIndex] = useState(0);
  const audioRef = useRef(new Audio());

  const togglePlay = () => {
    if (isPlaying) {
      audioRef.current.pause();
    } else {
      audioRef.current.src = tracks[currentTrackIndex].preview_url;
      audioRef.current.play();
    }
    setIsPlaying(!isPlaying);
  };

  const stopPlay = () => {
    audioRef.current.pause();
    audioRef.current.currentTime = 0;
    setIsPlaying(false);
  };

  audioRef.current.onended = () => {
    if (currentTrackIndex < tracks.length - 1) {
      setCurrentTrackIndex(currentTrackIndex + 1);
      audioRef.current.src = tracks[currentTrackIndex + 1].preview_url;
      audioRef.current.play();
    } else {
      setIsPlaying(false);
    }
  };

  return (
    <div className="playlist">
      <h2>Generated Playlist</h2>
      <div className="track-list">
        {tracks.map((track, index) => (
          <TrackCard 
            key={track.id} 
            track={track} 
            isActive={index === currentTrackIndex && isPlaying}
          />
        ))}
      </div>
      <div className="playlist-controls">
        <button className="control-btn play-pause" onClick={togglePlay}>
          {isPlaying ? '⏸' : '▶'}
        </button>
        <button className="control-btn stop" onClick={stopPlay}>⏹</button>
      </div>
    </div>
  );
}

export default Playlist;
