import React, { useState, useRef, useEffect } from 'react';
import '../styles/TrackCard.css';

function TrackCard({ track, isActive, onPlay, onStop }) {
  const [isPlaying, setIsPlaying] = useState(false);
  const [progress, setProgress] = useState(0);
  const audioRef = useRef(new Audio(track.preview_url));

  useEffect(() => {
    const audio = audioRef.current;

    const updateProgress = () => {
      setProgress((audio.currentTime / audio.duration) * 100);
    };

    audio.addEventListener('timeupdate', updateProgress);

    audio.addEventListener('ended', onNext);

    return () => {
      audio.removeEventListener('timeupdate', updateProgress);
      audio.removeEventListener('ended', onNext);
    };
  }, []);

  useEffect(() => {
    if (isActive && !isPlaying) {
      togglePlay();
    } else if (!isActive && isPlaying) {
      stopPlay();
    }
  }, [isActive]);

  const togglePlay = () => {
    if (isPlaying) {
      audioRef.current.pause();
    } else {
      audioRef.current.play();
      onPlay();
    }
    setIsPlaying(!isPlaying);
  };

  const stopPlay = () => {
    audioRef.current.pause();
    audioRef.current.currentTime = 0;
    setIsPlaying(false);
    setProgress(0);
    onStop();
  };

  return (
    <div className={`track-card ${isActive ? 'active' : ''}`}>
      <img src={track.album.images[0].url} alt={track.name} className="track-image" />
      <div className="track-info">
        <h3>{track.name}</h3>
        <p>{track.artists.map(artist => artist.name).join(', ')}</p>
        <div className="progress-bar">
          <div className="progress" style={{ width: `${progress}%` }}></div>
        </div>
      </div>
      <div className="track-controls">
        <button onClick={togglePlay}>{isPlaying ? '⏸' : '▶'}</button>
        <button onClick={stopPlay}>⏹</button>
      </div>
    </div>
  );
}

export default TrackCard;
